  const release = item.json;
  const urlParts = release.url ? release.url.split('/') : [];
  const repo_name = urlParts.length >= 6 ? `${urlParts[4]}/${urlParts[5]}` : 'Unknown';


  return {
    json: {
      repo_name: repo_name || 'Unknown',
      tag_name: release.tag_name,
      release_name: release.name,
      published_at: release.published_at,
      html_url: release.html_url,
      body: release.body || 'No release notes',
      author: release.author?.login || 'Unknown',
      is_prerelease: release.prerelease,
      // Clean up body text
      release_notes: (release.body || '')
        .replace(/#+/g, '')           // Remove markdown headers
        .replace(/\[.*?\]\(.*?\)/g, '') // Remove links
        .slice(0, 1000)                // Limit to 1000 chars for AI
    }
  };
});

return items.map(item => {
  const data = $('Extract Release Info').first().json
  return {
    json: {
      // The input data should still be accessible
      repo_name: data.repo_name,
      tag_name: data.tag_name,
      html_url: data.html_url,
      published_at: data.published_at,

      // AI response
      ai_summary: item.json.output
    }
  };
});

// The aggregate node gives us an array with a data property
const aggregatedData = items[0].json.data || items;

// Process each release
const releases = aggregatedData.map(item => {
  const ai_summary = item.ai_summary || 'No summary available';
  const hasFullData = item.html_url && item.published_at;

  return {
    repo: item.repo_name || 'Unknown',
    version: item.tag_name || 'Unknown',
    url: item.html_url || '#',
    summary: ai_summary,
    published: item.published_at 
      ? new Date(item.published_at).toLocaleDateString('en-US', {
          month: 'short',
          day: '2-digit',
          year: 'numeric'
        })
      : 'Unknown date',
    hasFullData: hasFullData
  };
});

// Filter out releases that don't have full data
const fullReleases = releases.filter(r => r.hasFullData);

// Group by category
const grouped = {
  features: [],
  bugfixes: [],
  breaking: [],
  other: []
};

fullReleases.forEach(r => {
  const category = (r.summary || '').toLowerCase();
  if (category.includes('feature')) {
    grouped.features.push(r);
  } else if (category.includes('bugfix') || category.includes('bug')) {
    grouped.bugfixes.push(r);
  } else if (category.includes('breaking')) {
    grouped.breaking.push(r);
  } else {
    grouped.other.push(r);
  }
});

